function Input(props) {
  const {
    inputEmpty,
    inputRegTel,
    inputEmptyClass,
    inputWrongClass,
    inputRegTelClass,
    labelName,
    labelClass,
    labelSpan,
    yearlySpan,
    spanClass,
    spanYearlyClass,
    typeInput,
    inputId,
    inputValue,
    placeholderInput,
    checked,
    onChange,
    // ref
  } = props;
console.log(inputWrongClass);

  return (
    <label className={labelClass}>
      {labelName}
      <span className={spanClass}>{labelSpan}</span>
      <span className={spanYearlyClass}>{yearlySpan}</span>
      <span className={inputEmptyClass}>{inputEmpty}</span>
      <span className={inputRegTelClass}>{inputRegTel}</span>
      <input
        className={inputWrongClass}
        checked={checked}
        onChange={onChange}
        type={typeInput}
        name={typeInput}
        id={inputId}
        value={inputValue}
        placeholder={placeholderInput}
        pattern={pattern}
        // required
      />
    </label>
  );
}

export default Input;
